'use strict'

/* -----------------------------------------------------------------------------
 * Target
 * -------------------------------------------------------------------------- */

const Target = function (options) {
  this.options = options
  this.name = 'target'
}

Target.options = {
  'target.defaultBrowser': { type: 'string', default: 'chrome' }
}

Target.prototype.get = function () {
  return ['device']
}

module.exports = Target
