'use strict'

/* -----------------------------------------------------------------------------
 * IntegrationPlugin
 * -------------------------------------------------------------------------- */

const IntegrationPlugin = function (options) {
  this.options = options
}

IntegrationPlugin.options = {
  'integrationPlugin.target': { type: 'string', default: 'target' }
}

IntegrationPlugin.prototype.run = function () {
  return 'integration'
}

module.exports = IntegrationPlugin
