'use strict';

/* -----------------------------------------------------------------------------
 * UnitPlugin
 * ---------------------------------------------------------------------------*/

const UnitPlugin = function (options) {
  this.options = options;
};

UnitPlugin.options = {
  'unitPlugin.target': { type: 'string', default: 'target' }
};

UnitPlugin.prototype.run = function () {
  return 'unit';
};

module.exports = UnitPlugin;
